import React, { useState } from "react";
import "./App.css";

import UploadCard from "./components/UploadCard";

function App() {
  const [status, setStatus] = useState("idle");
  const [filesToPush, setFilesToPush] = useState([]);

  const handleFiles = (files: FileList): void => {
    Object.values(files).forEach((file: File) => {
      if (isFileValid(file)) {
        setFilesToPush([...filesToPush, file]);
      }
    });
  };

  const isFileValid = (file: File): boolean => {
    const validTypes = ["image/jpeg", "image/png", "image/gif"];
    const maxSizeInBytes = 10e6;
    return validTypes.indexOf(file.type) !== -1 && file.size <= maxSizeInBytes;
  };

  const previewAndUpload = (files: Array<File>): void => {
    return;
  };

  return (
    <div className="App">
      <UploadCard handleFiles={handleFiles} />
    </div>
  );
}

export default App;
