import React, { isValidElement, useRef } from "react";
import style from "./style.module.scss";
import placeholder from "../../assets/imgs/placeholder.png";

const UploadCard = () => {
  const handleDrop = (e: any) => {
    const data = e.dataTransfer;
    const { files } = data;
    handleFiles(files);
  };

  const handleFiles = (files: FileList): void => {
    Object.values(files).forEach((file: File) => {
      if (isImageValid(file)) {
        handlePreViewAndUpload(file);
      }
    });
  };

  const isImageValid = (image: File): boolean => {
    const validTypes = ["image/jpeg", "image/png", "image/gif"];
    const maxSizeInBytes = 10e6;
    return (
      validTypes.indexOf(image.type) !== -1 && image.size <= maxSizeInBytes
    );
  };

  const handlePreViewAndUpload = (file: File): void => {
    return;
  };

  const preventDefault = (e: any) => {
    e.preventDefault();
    e.stopPropagation();
  };

  return (
    <div className={style["card"]}>
      <h3 className={style["card__title"]}>Upload your image</h3>
      <p className={style["card__description"]}>File should be Jpeg, Png...</p>
      <div
        className={style["card__drop-area"]}
        onDragEnter={(e) => preventDefault(e)}
        onDragOver={(e) => preventDefault(e)}
        onDragEnd={(e) => preventDefault(e)}
        onDrop={(e) => {
          preventDefault(e);
          handleDrop(e);
        }}
      >
        <img src={placeholder} alt="drop area" />
        <p>Drag & Drop you image here</p>
      </div>
      <p className={style["card__divider"]}>Or</p>
      <div>
        <input type="file" className={style["card__input"]}></input>
      </div>
    </div>
  );
};

export default UploadCard;
